const { Console } = require('console');
const eip = require('./dist/index.js');

let cont = new eip.Controller()





cont.connect('192.168.100.2',0 , true)
    .then(async () => { 

        let tag1 = cont.newTag('testBOOL6')
        await cont.readTag(tag1)

        tag1.value[0] = true

        await cont.writeTag(tag1)
        console.log(tag1.value)
        
        
    })
    .catch(e => {
        console.log(e);
    })
